def best_match(word, terms):
    candidates = [term for term in [term.strip() for term in terms.split(',')] if term]
    candidates = [{'nl': term.strip().capitalize(), 'weight': 100.0*float(model.wv.similarity(word, term.strip()))} for term in candidates]
    return sorted(candidates, key=lambda x: -x['weight'])[0]

def similarity(word):
    return {field: {concept: best_match(word, nl) for concept, nl in concepts[field].items()} for field in concepts}


app = flask.Flask('similarity')

@app.route('/<word>')
def sim(word):
    return json.dumps(similarity(word))

